import React, { Component } from 'react'
import './../../styles/absensi.css'

import Header from '../global/header'
import MenuBar from '../global/navbar'

import { Table, TabContent, TabPane, Nav, NavItem, NavLink } from 'reactstrap'
import classnames from 'classnames'

export default class Absensi extends Component {
  constructor(props) {
    super(props);
    this.state = { value: 'coconut' };

    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }
  handleSubmit(event) {

  }
  render() {
    return (
      <div>
        <Header></Header>
        <MenuBar></MenuBar>
        <br /><br />
        <div className="content">
          <div className="row">
            <div className="left-content col-2">
              <h5><strong>Urutkan Berdasarkan</strong></h5>
              <br />
              <form onSubmit={this.handleSubmit}>
                <select value={this.state.value} onChange={this.handleChange}>
                  <option value="teratas">Peringkat Teratas</option>
                  <option value="ascending">Nama Murid (A ke Z)</option>
                  <option value="terbawah">Kehadiran Terendah</option>
                </select>
                <date-input date="{{date}}" timezone="[[timezone]]"></date-input>
                <br /><br />
                <button type="submit" className="btn-green">Urutkan</button>
              </form>
            </div>
            <div className="center-content col-8">

            </div>
          </div>
        </div>
      </div>
    )
  }
}
