import React, { Component } from 'react'
import './../../styles/daftar-nilai.css';

import Header from '../global/header'
import MenuBar from '../global/navbar'

import { Table, TabContent, TabPane, Nav, NavItem, NavLink, Card, Button, CardTitle, CardText, Row, Col } from 'reactstrap';
import classnames from 'classnames';

class DaftarNilai extends Component {
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            activeTab: '1'
        };
    }

    toggle(tab) {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    }
    render() {
        return (
            <div className="nilai">
                <Header></Header>
                <MenuBar></MenuBar>
                <br /><br />
                <div className="content">
                    <div className="row">
                        <div className="left-content col-2">
                            <h6><strong>Filter</strong></h6>
                            <br />
                            <form onSubmit={this.handleSubmit}>
                                <select value={this.state.value} onChange={this.handleChange}>
                                    <option value="teratas">Peringkat Teratas</option>
                                    <option value="ascending">Nama Murid (A ke Z)</option>
                                    <option value="terbawah">Kehadiran Terendah</option>
                                </select>
                                <br /><br />
                                <button type="submit" className="btn-green">Urutkan</button>
                            </form>
                        </div>
                        <div className="center-content col-8 row">
                            <h6 className="col-7"><strong>Kelas X IPA 2</strong></h6>
                            <span className="float-right col-5">
                                <Nav tabs>
                                    <NavItem className="tab-nilai">
                                        <NavLink
                                            className={classnames({ active: this.state.activeTab === '1' })}
                                            onClick={() => { this.toggle('1'); }}>
                                            Pengetahuan
                                        </NavLink>
                                    </NavItem>
                                    <NavItem className="tab-nilai">
                                        <NavLink
                                            className={classnames({ active: this.state.activeTab === '2' })}
                                            onClick={() => { this.toggle('2'); }}>
                                            Keterampilan
                                        </NavLink>
                                    </NavItem>
                                    <NavItem className="tab-nilai">
                                        <NavLink
                                            className={classnames({ active: this.state.activeTab === '3' })}
                                            onClick={() => { this.toggle('3'); }}>
                                            Sikap
                                        </NavLink>
                                    </NavItem>
                                </Nav>
                            </span>
                            <br />
                        </div>
                    </div>
                    <br /><br /><br />
                </div>
            </div>
        )
    }
}
export default DaftarNilai;
